head.script:
//

            /* Show error message if cookies are disabled */
            $(document).ready(function() {
            if (navigator.cookieEnabled == false) {
                var attentionText = "Note:";
                var attentionNoCookie = "Your browser does not support cookies. Please allow cookies to be able to use all the features of our site."; 
                var attentionMessage = '<div id="nocookie"><p><strong>' + attentionText + ':</strong> '+ attentionNoCookie +'</p></div>';
                $('body').prepend(attentionMessage);
            }});

//

body.script:
//

                        $(document).ready(function() {
                            var cookieBanner = $("#cookie-banner");
                            if($("#cookie-banner").length  ) {
                                var cookieBannerHeight = $(cookieBanner).height();
                                if($.cookie("cookieNotificationAccepted") != "true")
                                {
                                    $("body").css("background-position", "center "+cookieBannerHeight+"px");
                                    $("#cookie-banner").show();
                                }
                                $("#cookie-banner-ok-btn").click(function() {
                                    $.cookie("cookieNotificationAccepted", "true", { expires:30 , path: "/" });
                                    $("#cookie-banner").hide();
                                    $("body").css("background-position", "center 0px");
                                });
                            }
                        });

